using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace ProjectEuler.Problem8
{
	class Program
	{
		/// <summary>
		/// 1000桁の数字から5つの連続する数字を取り出して その積を計算する。その積の中で最大の値を求める。
		/// </summary>
		/// <param name="args"></param>
		static void Main(string[] args)
		{
			string n = "";
			int digit = 5;

			// ゼロが入ると積がゼロになるので、ゼロで分割を行います。
			var splitByZero = n.Split('0');

			// 指定桁以下の文字列は対象外なので、指定桁以上のもののみ抽出します。
			var targetTexts = splitByZero.Where(s => s.Length >= digit);

			// 対象となった文字列から順に5桁ずつ取り出し、積を計算して蓄積していきます。
			var multiples = new List<int>();

			foreach (var text in targetTexts)
			{
				var numberOfTargets = (text.Length - digit + 1);

				for (int i = 0; i < numberOfTargets; i++)
				{
					var extracted = text.Substring(i, digit);
					var multiple = extracted.ToArray()
						.Select(s => int.Parse(new string(s, 1)))
						.Aggregate((x, y) => x * y);

					multiples.Add(multiple);
				}
			}

			// 最大の積が答えになります。
			var answer = multiples.Max();
			Console.WriteLine(answer);

			Console.ReadLine();
		}
	}
}
